package com.leo.shareloginpay.payimport android.text.TextUtilsclass AliPayResultBean(rawResult: String) {    var resultStatus: String? = null    private var result: String? = null    private var memo: String? = null    init {        val resultParams = rawResult.split(";".toRegex()).dropLastWhile { it.isEmpty() }.toTypedArray()        for (resultParam in resultParams) {            if (resultParam.startsWith("resultStatus")) {                resultStatus = gatValue(resultParam, "resultStatus")            }            if (resultParam.startsWith("result")) {                result = gatValue(resultParam, "result")            }            if (resultParam.startsWith("memo")) {                memo = gatValue(resultParam, "memo")            }        }    }    override fun toString(): String {        return ("resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}")    }    private fun gatValue(content: String, key: String): String {        val prefix = "$key={"        return content.substring(content.indexOf(prefix) + prefix.length,                content.lastIndexOf("}"))    }    /**     * @return the memo     */    fun getMemo(): String {        return if (TextUtils.isEmpty(memo)) "" else memo!!    }    /**     * @return the result     */    fun getResult(): String {        return if (TextUtils.isEmpty(result)) "" else result!!    }}